    event.preventDefault()
    fetch(url, {
      method: "POST",
      body: JSON.stringify({
        feeling:"",
        messagePeople:questions3,
        messageGrateful: questions4
      }),
      headers: { "Content-Type": "application/json" }
    })
    .then((res) => res.json())
        .then((json) => {
            // setReflection(json)
            console.log(json)
    props.onFormSubmit(message)
    setMessage("")
    })}  
