      <div className="header2">
        <h1>Time to reflect</h1>
      </div> 

      {!submitted && (
      <form className='form' onSubmit={event => event.preventDefault()}>

      <section className="boxes">
        <div className="questions1">
          {questionsGroup2.map(group => (
            <label key={group}>
